SELECT DailyAvailability.EmployeeID, Employees.FName, Employees.LName, DailyAvailability.DayOfWeek, DailyAvailability.StartTime, DailyAvailability.EndTime FROM DailyAvailability JOIN Employees ON Employees.EmployeeID = DailyAvailability.EmployeeID ORDER BY EmployeeID, (CASE  WHEN DayOfWeek = 'DailyDefault' THEN 0 WHEN DayOfWeek = 'Sunday' THEN 1 WHEN DayOfWeek = 'Monday' THEN 2 WHEN DayOfWeek = 'Tuesday' THEN 3 WHEN DayOfWeek = 'Wednesday' THEN 4 WHEN DayOfWeek = 'Thursday' THEN 5 WHEN DayOfWeek = 'Friday' THEN 6 WHEN DayOfWeek = 'Saturday' THEN 7 END)
